function triangle(baseValue,heightValue){


    // calculate the area
    const triangleareaValue = (baseValue * heightValue) / 2;

    return triangleareaValue
   
}

exports.triangle = triangle;

function circle(radiusValue){

    // calculate the area
    const circleareaValue = (radiusValue * radiusValue * Math.PI);
   
    return circleareaValue
}  

exports.circle = circle;


function square(squareLength){
    const squareareaValue = (squareLength * squareLength);

    return squareareaValue
}

exports.square = square;
